import { MOVE_ITEM } from '../types'

const initialState = {
    breakfast: [],
    morningSnack: [],
    lunch: [],
    afternoonSnack: [],
    dinner: []
}

export const categoriesReducer = (state = initialState, action) => {
    switch (action.type) {
        case MOVE_ITEM: 
            return {
                ...state,

            }
        default:
            return state
    }
}
