import { ADD_ITEM, REMOVE_ITEM } from "../types";

export const addItem = (obj, category) => ({
    type: ADD_ITEM,
    payload: {obj, category}
})

export const removeItem = (id, category) => ({
    type: REMOVE_ITEM,
    payload: {id, category}
})
