import { SET_MODAL_VISIBILITY } from "../types"

const initialState = {
    visible: false
}

export const modalListReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_MODAL_VISIBILITY:
            return {
                ...state,
                visible: action.payload
            }
    }
}
